
const REQUIRED_VARS = [
  'PORT',
  'YOUTUBE_DATA_API_KEY',
];

if (process.env.NODE_ENV !== 'production') {
  require('dotenv/config');
}

const get = (name) => {
  const value = process.env[name];
  if (!value) {
    throw new Error(`Missing environment variable ${name}`);
  }
  return value;
}

REQUIRED_VARS.forEach((name) => {
  exports[name] = get(name);
});
